import React, { useState, useEffect } from 'react';
import '../../styles/analytics.css';
import axios from 'axios';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement } from 'chart.js';

// Register necessary components for Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Analytics = () => {
  const [quizPerformance, setQuizPerformance] = useState([]);
  const [userActivity, setUserActivity] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAnalyticsData = async () => {
      try {
        const [quizPerformanceResponse, userActivityResponse] = await Promise.all([
          axios.post('http://localhost:5001/viewQuizPerformance', { eventID: "1001" }),
          axios.post('http://localhost:5001/viewUserActivity', { eventID: "1001" })
        ]);

        if (quizPerformanceResponse.data.rData && quizPerformanceResponse.data.rData.items) {
          setQuizPerformance(quizPerformanceResponse.data.rData.items);
        }

        if (userActivityResponse.data.rData && userActivityResponse.data.rData.items) {
          setUserActivity(userActivityResponse.data.rData.items);
        }

        setLoading(false);
      } catch (error) {
        console.error('Error fetching analytics data:', error);
        setLoading(false);
      }
    };

    fetchAnalyticsData();
  }, []);

  // Prepare data for charts
  const quizPerformanceData = {
    labels: quizPerformance.map(quiz => quiz.quiz_title),
    datasets: [{
      label: 'Total plays of the quiz',
      data: quizPerformance.map(quiz => quiz.times_played),
      backgroundColor: [
        'rgba(75, 192, 192, 0.2)',
        'rgba(153, 102, 255, 0.2)',
        'rgba(255, 159, 64, 0.2)',
        'rgba(255, 99, 132, 0.2)',
        'rgba(54, 162, 235, 0.2)',
        'rgba(255, 206, 86, 0.2)',
      ],
      borderColor: [
        'rgba(75, 192, 192, 1)',
        'rgba(153, 102, 255, 1)',
        'rgba(255, 159, 64, 1)',
        'rgba(255, 99, 132, 1)',
        'rgba(54, 162, 235, 1)',
        'rgba(255, 206, 86, 1)',
      ],
      borderWidth: 1,
    }],
  };

  const userActivityData = {
    labels: userActivity.map(user => user.username),
    datasets: [{
      label: 'Total Quizzes Taken',
      data: userActivity.map(user => user.total_quizzes_taken),
      backgroundColor: 'rgba(153, 102, 255, 0.2)',
      borderColor: 'rgba(153, 102, 255, 1)',
      borderWidth: 1,
    }],
  };

  return (
    <div className="analytics-container">
      <h2>Analytics</h2>

      {loading ? (
        <p>Loading...</p>
      ) : (
        <>
          <section>
            <h3>Quiz Performance</h3>
            <Pie data={quizPerformanceData} options={{
              responsive: true,
              plugins: {
                title: {
                  display: true,
                  text: 'Quiz Performance'
                },
                legend: {
                  position: 'top',
                },
              },
            }} />
          </section>

          <section>
            <h3>User Activity</h3>
            <Bar data={userActivityData} options={{
              responsive: true,
              plugins: {
                title: {
                  display: true,
                  text: 'User Activity'
                },
                legend: {
                  position: 'top',
                },
              },
              scales: {
                x: {
                  beginAtZero: true,
                },
                y: {
                  beginAtZero: true,
                },
              },
            }} />
          </section>
        </>
      )}
    </div>
  );
};

export default Analytics;
