const renderQuizQuestions = (quiz) => {
  // Ensure quiz and quiz.questions are defined before rendering
  if (!quiz || !quiz.questions) {
    return <p>No quiz questions available</p>;
  }

  return (
    <div className="quiz-details">
      <h3>{quiz.title}</h3>
      <p>{quiz.description}</p>
      <div className="timer">Timer: {timer} seconds</div>
      <ul>
        {quiz.questions.map((q) => (
          <li key={q.quiz_id}>
            <p>{q.question}</p>
            <ul>
              {q.options.map((option, index) => {
                let className = '';
                if (isSubmitted) {
                  if (option === q.correct_answer) {
                    className = 'correct-option';
                  } else if (selectedOptions[q.quiz_id] === option) {
                    className = 'incorrect-option';
                  }
                } else if (selectedOptions[q.quiz_id] === option) {
                  className = 'selected-option';
                }
                return (
                  <li
                    key={index}
                    className={className}
                    onClick={() => handleOptionSelect(q.quiz_id, option)}
                  >
                    {option}
                  </li>
                );
              })}
            </ul>
          </li>
        ))}
      </ul>
      <button onClick={handleSubmitQuiz}>Submit</button>
      <button onClick={() => setSelectedQuiz(null)}>Back to Quizzes</button>
    </div>
  );
};
