import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from './Modal'; // Assuming Modal component is defined

const QuizList = () => {
  const [quizzes, setQuizzes] = useState([]);
  const [selectedQuiz, setSelectedQuiz] = useState(null);
  const [selectedOptions, setSelectedOptions] = useState({});
  const [timer, setTimer] = useState(null);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [score, setScore] = useState(0);
  const [showModal, setShowModal] = useState(false);
  const [correctAnswers, setCorrectAnswers] = useState(0);
  const [incorrectAnswers, setIncorrectAnswers] = useState(0);

  useEffect(() => {
    const fetchQuizzes = async () => {
      try {
        const response = await axios.post('http://localhost:5164/viewCardQuiz', { eventID: "1001" });
        if (response.status === 200) {
          const responseData = response.data;
          if (responseData.rData && responseData.rData.items) {
            setQuizzes(responseData.rData.items);
            console.log("Quizzes:", responseData.rData.items);
          } else {
            console.log("No quizzes data in response");
          }
        } else {
          console.log("Failed to fetch quizzes - status:", response.status);
        }
      } catch (error) {
        console.error("Error fetching quizzes:", error);
      }
    };

    fetchQuizzes();
  }, []);

  const handleStartQuiz = async (quiz) => {
    try {
      setSelectedQuiz(quiz);
      setSelectedOptions({});
      setTimer(0); // Reset timer when starting a new quiz
      setIsSubmitted(false); // Reset submission state
      setScore(0); // Reset score
      setShowModal(false); // Hide modal if open
      setCorrectAnswers(0);
      setIncorrectAnswers(0);

      const response = await axios.post('http://localhost:5164/viewQuiz', { eventID: "1001", quiz_card_id: quiz.quiz_card_id });
      if (response.status === 200) {
        const responseData = response.data;
        if (responseData.rData && responseData.rData.items) {
          setSelectedQuiz({ ...quiz, questions: responseData.rData.items });
          console.log("Quiz Questions:", responseData.rData.items);
        } else {
          console.log("No quiz questions data in response");
        }
      } else {
        console.log("Failed to fetch quiz questions - status:", response.status);
      }

      const startTime = Date.now();
      const interval = setInterval(() => {
        const elapsedTime = Date.now() - startTime;
        setTimer(Math.floor(elapsedTime / 1000)); // Update timer every second
      }, 1000);

      return () => clearInterval(interval); // Cleanup on unmount or change
    } catch (error) {
      console.error("Error starting quiz:", error);
    }
  };

  const handleOptionSelect = (questionId, option) => {
    setSelectedOptions({
      ...selectedOptions,
      [questionId]: option,
    });
  };

  const handleSubmitQuiz = () => {
    if (!selectedQuiz || !selectedQuiz.questions) {
      console.error("No quiz or quiz questions available.");
      return;
    }

    let newScore = 0;
    let correctCount = 0;
    let incorrectCount = 0;
    selectedQuiz.questions.forEach(question => {
      if (selectedOptions[question.quiz_id] === question.correct_answer) {
        newScore += 1;
        correctCount += 1;
      } else {
        incorrectCount += 1;
      }
    });
    setScore(newScore);
    setCorrectAnswers(correctCount);
    setIncorrectAnswers(incorrectCount);
    setIsSubmitted(true);
    setShowModal(true); // Show modal when quiz is submitted
  };

  const renderQuizQuestions = (quiz) => {
    // Step 1: Check if quiz or quiz.questions are undefined
    if (!quiz || !quiz.questions || quiz.questions.length === 0) {
      console.log('No quiz or quiz questions available');
      return (
        <div className="quiz-details">
          <p>No quiz questions available</p>
        </div>
      );
    }

    // Step 2: Log the quiz details to ensure we have the correct data
    console.log('Render Quiz:', quiz);
    console.log('Quiz Title:', quiz.title);
    console.log('Quiz Questions:', quiz.questions);

    // Step 3: Render the quiz details and questions
    return (
      <div className="quiz-details">
        <h3>{quiz.title}</h3>
        {/* Optional: Render quiz description if available */}
        {quiz.description && <p>{quiz.description}</p>}
        {/* Optional: Render quiz timer if available */}
        {quiz.timer && <div className="timer">Timer: {quiz.timer} seconds</div>}
        <ul>
          {/* Step 4: Map over quiz questions and render each question */}
          {quiz.questions.map((q) => (
            <li key={q.quiz_id}>
              <p>{q.question}</p>
              <ul>
                {/* Step 5: Map over options of each question and render */}
                {q.options.map((option, index) => {
                  let className = '';
                  if (isSubmitted) {
                    if (option === q.correct_answer) {
                      className = 'correct-option';
                    } else if (selectedOptions[q.quiz_id] === option) {
                      className = 'incorrect-option';
                    }
                  } else if (selectedOptions[q.quiz_id] === option) {
                    className = 'selected-option';
                  }
                  // Step 6: Render each option with appropriate class and onClick handler
                  return (
                    <li
                      key={index}
                      className={className}
                      onClick={() => handleOptionSelect(q.quiz_id, option)}
                    >
                      {option}
                    </li>
                  );
                })}
              </ul>
            </li>
          ))}
        </ul>
        {/* Optional: Add a button to submit the quiz */}
        <button onClick={handleSubmitQuiz}>Submit</button>
        {/* Optional: Add a button to go back to quizzes */}
        <button onClick={() => setSelectedQuiz(null)}>Back to Quizzes</button>
      </div>
    );
  };








  return (
    <div className="quiz-list">
      <h2>Available Quizzes</h2>
      {!selectedQuiz ? (
        <ul>
          {quizzes.map(quiz => (
            <li key={quiz.quiz_card_id}>
              <h3>{quiz.title}</h3>
              <p>{quiz.no_of_questions} Questions</p>
              <button onClick={() => handleStartQuiz(quiz)}>Start Quiz</button>
            </li>
          ))}
        </ul>
      ) : (
        renderQuizQuestions(selectedQuiz)
      )}
      <Modal
        isOpen={showModal}
        onClose={() => setShowModal(false)}
        correctAnswers={correctAnswers}
        incorrectAnswers={incorrectAnswers}
        score={score}
      />
    </div>
  );
};

export default QuizList;
