const QuizList = () => {
  const [showTimeUpModal, setShowTimeUpModal] = useState(false); // State for time's up modal

  const startTimer = () => {
    if (timerRef.current) {
      clearInterval(timerRef.current);
    }
    setTimer(5); // Reset timer to initial value (e.g., 5 seconds)
    timerRef.current = setInterval(() => {
      setTimer(prev => {
        if (prev <= 1) {
          clearInterval(timerRef.current);
          setShowTimeUpModal(true); // Show time's up modal
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  // ... rest of your code

  return (
    <div className="quiz-list">
      {/* Existing JSX */}
      
      {/* Time's Up Modal */}
      {showTimeUpModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Time's Up!</h2>
            <p>Time is over. Please submit your quiz.</p>
            <button onClick={() => {
              setShowTimeUpModal(false); // Close time's up modal
              handleSubmitQuiz(); // Handle quiz submission
            }}>Submit Quiz</button>
          </div>
        </div>
      )}

      {/* Existing Modal component */}
      <Modal
        isOpen={showModal}
        onClose={() => setShowModal(false)}
        correctAnswers={correctAnswers}
        incorrectAnswers={incorrectAnswers}
        score={score}
        userId={user.user_id}
        quizCardId={selectedQuiz ? selectedQuiz.id : null}
        handleSubmit={handleSubmitQuiz}
      />
    </div>
  );
};
