import React from 'react';
// import './Modal.css'; // Ensure you have CSS styles for modal

const Modal = ({ isOpen, onClose, correctAnswers, incorrectAnswers, score, userId, quizCardId, handleSubmit }) => {
  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Time is Over</h2>
        <p>Your quiz has ended.</p>
        <p>Score: {score}</p>
        <p>Correct Answers: {correctAnswers}</p>
        <p>Incorrect Answers: {incorrectAnswers}</p>
        {handleSubmit && <button onClick={handleSubmit}>Submit</button>}
        <button onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default Modal;
