import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../../styles/userManagement.css';

const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const [confirmDelete, setConfirmDelete] = useState(null);
  const [selectedUser, setSelectedUser] = useState(null); // Track which user's results to show

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.post('http://localhost:5164/viewUsers', { eventID: "1001" });
        if (response.status === 200) {
          const responseData = response.data;
          if (responseData.rData && responseData.rData.items) {
            setUsers(responseData.rData.items);
            console.log("Users:", responseData.rData.items);
          } else {
            console.log("No users data in response");
          }
        }
      } catch (error) {
        console.error("Error fetching users:", error);
      }
    };

    fetchUsers();
  }, []);

  const handleDelete = async (user_id) => {
    try {
      const response = await axios.post('http://localhost:5164/deleteUsers', {
        eventID: "1001",
        addInfo: {
          user_id: user_id
        }
      });

      console.log("Delete Response:", response.data); // Log the entire response

      if (response.status === 200) {
        const responseData = response.data;
        if (responseData.rData.rMessage === "DELETE SUCCESSFULLY.") {
          setUsers(users.filter(user => user.user_id !== user_id)); // Remove user from local state
          console.log(`User with ID ${user_id} deleted successfully`);
        } else {
          console.log("Failed to delete user");
        }
      }
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  const handleConfirmDelete = (index) => {
    setConfirmDelete(index);
  };

  const handleCancelDelete = () => {
    setConfirmDelete(null);
  };

  const handleViewResults = (user) => {
    setSelectedUser(user);
  };

  const handleHideResults = () => {
    setSelectedUser(null);
  };

  return (
    <div>
      <h2>User Management</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Username</th>
            <th>Email</th>
            <th>Address</th>
            <th>Picture</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, index) => (
            <React.Fragment key={user.user_id}>
              <tr>
                <td>{user.name}</td>
                <td>{user.username}</td>
                <td>{user.email}</td>
                <td>{user.address}</td>
                <td>
                  <img src={`data:image/png;base64, ${user.picture}`} alt="Profile Image" width={60} />
                </td>
                <td>
                  {confirmDelete === index ? (
                    <div>
                      <button className="delete-button" onClick={() => handleDelete(user.user_id)}>
                        Confirm Delete
                      </button>
                      <button className="delete-button" onClick={handleCancelDelete}>
                        Cancel
                      </button>
                    </div>
                  ) : (
                    <div>
                      <button className="delete-button" onClick={() => handleConfirmDelete(index)}>
                        Delete
                      </button>
                      <button className="view-button" onClick={() => handleViewResults(user)}>
                        View
                      </button>
                    </div>
                  )}
                </td>
              </tr>
              {selectedUser && selectedUser.user_id === user.user_id && (
                <tr>
                  <td colSpan="6">
                    <h3>Quiz Results for {selectedUser.name}</h3>
                    <p>Email: {selectedUser.email}</p>
                    {/* Render quiz results here */}
                    {/* For now, just a placeholder */}
                    <div>
                      Quiz 1: Score 80%<br />
                      Quiz 2: Score 90%<br />
                    </div>
                    <button onClick={handleHideResults}>Hide Results</button>
                  </td>
                </tr>
              )}
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserManagement;
